  SUBROUTINE quadsTOTRI()
  use m_netw
  use gridoperations
  implicit none
  double precision :: a
  integer :: k0
  integer :: k1
  integer :: k2
  integer :: k3
  integer :: k4
  integer :: l
  integer :: l12
  integer :: np
  integer :: numtri
  double precision :: r
  DOUBLE PRECISION DLENGTH

  CALL FINDcells(4)  ! quads

  L = NUMTRI
  DO NP = 1,NUMP
     K1 = netcell(NP)%NOD(1)
     K2 = netcell(NP)%NOD(2)
     K3 = netcell(NP)%NOD(3)
     K4 = netcell(NP)%NOD(4)

     CALL FINDEL(K1,K2,L12)
     A = 0 ! EA(L12)
     R = DLENGTH(K1,K2)
     CALL CONNECT(K1,K3,1,A,R)

     L  = L + 1
     K0 = 1 + (L-1)*3
     KTRI(K0)   = K1
     KTRI(K0+1) = K2
     KTRI(K0+2) = K3

     L  = L + 1
     K0 = 1 + (L-1)*3
     KTRI(K0)   = K4
     KTRI(K0+1) = K1
     KTRI(K0+2) = K3
  ENDDO
  NUMTRI = K0+2

  RETURN
  END SUBROUTINE quadsTOTRI

  SUBROUTINE GETMIDDLEKNOT(K1,K2,K12,A12,R12)
  use m_netw
  use gridoperations
  implicit none
  integer :: K1,K2,K12,K22
  double precision :: A12, R12

  integer :: l1
  integer :: l2
  integer :: n1
  integer :: n2

  DO N1  = 1,NMK(K1)
     L1  = NOD(K1)%LIN(N1)
     CALL OTHERNODE(K1,L1,K12)
     DO N2 = 1,NMK(K2)
        L2 = NOD(K2)%LIN(N2)
        CALL OTHERNODE(K2,L2,K22)
        IF (K12 .EQ. K22) THEN
           A12 = 0 ! ( EA(L1) + EA(L2) ) /2
           R12 = 0 ! ( RL(L1) + RL(L2) ) /2
           RETURN
        ENDIF
     ENDDO
  ENDDO
  K12 = 0
  RETURN
  END SUBROUTINE GETMIDDLEKNOT


  !> Finds the net link number between two net nodes.
  SUBROUTINE FINDEL(K1,K2,L1)
  use m_netw
  implicit none
  integer, intent(in   ) :: K1, K2 !< The two net node numbers between which a net link is searched for.
  integer, intent(  out) :: L1 !< The shared netlink between nodes k1 and k2, or 0 when not found.

  integer :: l2
  integer :: n1
  integer :: n2

  DO N1 = 1,NMK(K1)
     L1 = NOD(K1)%LIN(N1)
     DO N2 = 1,NMK(K2)
        L2 = NOD(K2)%LIN(N2)
        IF (L1 .EQ. L2) RETURN
     ENDDO
  ENDDO
  L1 = 0
  RETURN
  END SUBROUTINE FINDEL

  SUBROUTINE ALREADYTRI(K1,K2,K3,JA)
  use m_netw
  implicit none
  integer :: K1,K2,K3,JA

  integer :: n1
  integer :: n2
  integer :: n3
  integer :: np
  JA = 0
  DO NP = NUMP, 1, -1
     IF (netcell(NP)%N .EQ. 3) THEN
        N1 = netcell(NP)%NOD(1)
        N2 = netcell(NP)%NOD(2)
        N3 = netcell(NP)%NOD(3)
        IF ((K1.EQ.N1 .OR. K1.EQ.N2  .OR. K1.EQ.N3 ) .AND. &
            (K2.EQ.N1 .OR. K2.EQ.N2  .OR. K2.EQ.N3 ) .AND. &
            (K3.EQ.N1 .OR. K3.EQ.N2  .OR. K3.EQ.N3 ) ) THEN
            JA = np
            call qnerror('already 3', ' ',' ')
            RETURN
        ENDIF
     ENDIF
  ENDDO
  RETURN
  END SUBROUTINE ALREADYTRI


