             znod = dmiss
          else if (jawind>0 .and. jawsource>0) then
             znod = wsorE(itheta_view, kk)
          end if
       case(26)
           if (jawave .ne. 4 ) then
               znod = dmiss
           else
               znod = sigt(itheta_view,kk)
           end if
       case(27)
           if (jawave .ne. 4 ) then
               znod = dmiss
           elseif (windmodel.eq.0) then
               znod = cgwav(kk)
           else
               znod = cgwavt(itheta_view,kk)
           end if
       case(28)
          if (jawave == 1 .or. jawave == 2) then
             znod = fetch(1,kk)
