               znod = ma(itheta_view,kk)
           endif


     end select

    endif

 else if (nodval == numoptsf .and. jasecflow > 0) then
    select case (ispirparopt)
       case (1)
          if ( jasecflow > 0 ) then
             znod = spircrv(kk)
          else
             znod = bz(kk)
