 if (jasal > 0 .or. jatem > 0 .or. jased> 0 .or. stm_included ) then
    if (abs(jabaroctimeint) >= 2) then
       if (jacreep == 1 .or. abs(jabaroctimeint) == 3 .or. abs(jabaroctimeint) == 4) then
