    else                   ! 3D
       do i  = 1, nbnds
          ki = kbnds(2,i)
          call getkbotktop(ki,kb,kt)
             do k = kb, kt
             sigmabnds(kmx*(i-1)+k-kb+1) = (0.5d0*(zws(k-1)+zws(k))-zws(kb-1)) / max(epshs, (zws(kt)-zws(kb-1)) )
             end do
