             end do
       end do

       if ( stm_included ) then
          ! sediment boundaries
          do isf=1,numfracs
             do i=1,nbndsf(isf)
                ki = bndsf(isf)%k(2,i)
                call getkbotktop(ki,kb,kt)
                do k=kb,kt
                   bndsf(isf)%sigma(kmx*(i-1)+k-kb+1) = (0.5d0*(zws(k-1)+zws(k))-zws(kb-1)) / max(epshs, (zws(kt)-zws(kb-1)) )
                end do
                !            SPvdP: fill remainder
                do k=kt+1,kb+kmx-1
                   bndsf(isf)%sigma(kmx*(i-1)+k-kb+1) = 1d0
                end do
             end do
          end do
       end if
    end if

