 iresult = DFM_NOERR

 success = .true.    ! default if no valid providers are present in *.ext file (m_flowexternalforcings::success)

 ! (re-)initialize flags/counters related to temperature forcings
 itempforcingtyp = 0
 btempforcingtypA = .false.
