       xy2bndz(:,k) = xyen(:,L)

       kbndz(1,k) = kb
       kbndz(2,k) = kbi
       kbndz(3,k) = Lf
       kbndz(4,k) = itpez(k)
       kbndz(5,k) = itpenz(k)
       kbndz(6,k) = ftpet(k)

       !! hier vullen

       lnxbnd(Lf-lnxi) = itpenz(k)

       do n = 1,nd(kbi)%lnx
          L = iabs(nd(kbi)%ln(n))
          teta(L) = 1d0
       enddo

       if (iadvec /=0 .and. kcu(L) == -1) then
          iad = iadvec1D
       else
