          if (exist) then
             filetype0 = uniform            ! uniform=single time series vectormax = kx = 3
             success  = ec_addtimespacerelation(qid, xdum, ydum, kdum, kx, filename0, filetype0, method=spaceandtime, operand='O', targetIndex=ncgensg)
