    allocate( pumponoff(5,npumpsg) ) ; pumponoff = dmiss


    do n = 1, npumpsg

       pumponoff(5,n) = 0
       do k = L1pumpsg(n), L2pumpsg(n)
          L             = kep(k)
          Lf            = iabs(L)
          if (L > 0) then
             kb         = ln(1,Lf)
             kbi        = ln(2,Lf)
          else
             kb         = ln(2,Lf)
             kbi        = ln(1,Lf)
          endif
          kpump(1,k)    = kb
          kpump(2,k)    = kbi
          kpump(3,k)    = L ! f

          xpump(n)      = xz(kb)
          ypump(n)      = yz(kb)
          xy2pump(1,n)  = xz(kbi)
          xy2pump(2,n)  = yz(kbi)
       enddo

